pragma solidity ^0.6.0;

import "./interfaces/MarketControllerInterface.sol";
import "./interfaces/PriceOracleInterface.sol";
import "./libraries/ErrorReporter.sol";
import "./libraries/Exponential.sol";
import "./HToken.sol";
import "./HErc20.sol";
import "./HDS.sol";
import "./DOL.sol";

/**
 * @title Hades' MarketController Contract
 * @author Hades
 */
contract MarketController is MarketControllerInterface, MarketControllerErrorReporter, Exponential {
	/**
	 * @notice Oracle which gives the price of any given asset
	 */
	PriceOracleInterface public oracle;

	/**
	 * @notice Multiplier used to calculate the maximum repayAmount when liquidating a borrow
	 */
	uint256 public closeFactorMantissa;

	/**
	 * @notice Multiplier representing the discount on collateral that a liquidator receives
	 */
	uint256 public liquidationIncentiveMantissa;

	/**
	 * @notice Default collateral factor
	 */
	uint256 public defaultCollateralFactorMantissa;

	/**
	 * @notice Max number of assets a single account can participate in (borrow or use as collateral)
	 */
	uint256 public maxAssets;

	/**
	 * @notice Per-account mapping of "assets you are in", capped by maxAssets
	 */
	mapping(address => HToken[]) public accountAssets;

	struct Market {
		/// @notice Whether or not this market is listed
		bool isListed;
		/**
		 * @notice Multiplier representing the most one can borrow against their collateral in this market.
		 *  For instance, 0.9 to allow borrowing 90% of collateral value.
		 *  Must be between 0 and 1, and stored as a mantissa.
		 */
		uint256 collateralFactorMantissa;
		/// @notice Per-market mapping of "accounts in this asset"
		mapping(address => bool) accountMembership;
	}

	/**
	 * @notice Official mapping of hTokens -> Market metadata
	 * @dev Used e.g. to determine if a market is supported
	 */
	mapping(address => Market) public markets;

	/**
	 * @notice The Pause Guardian can pause certain actions as a safety mechanism.
	 *  Actions which allow users to remove their own assets cannot be paused.
	 *  Liquidation / seizing / transfer can only be paused globally, not by market.
	 */
	address public pauseGuardian;
	bool public _mintGuardianPaused;
	bool public _borrowGuardianPaused;
	bool public transferGuardianPaused;
	bool public seizeGuardianPaused;
	mapping(address => bool) public mintGuardianPaused;
	mapping(address => bool) public borrowGuardianPaused;

	/// @notice A list of all markets
	HToken[] public allMarkets;

	address public admin;
	address public superior;

	// closeFactorMantissa must be strictly greater than this value
	uint256 internal constant closeFactorMinMantissa = 0.05e18; // 0.05

	// closeFactorMantissa must not exceed this value
	uint256 internal constant closeFactorMaxMantissa = 0.9e18; // 0.9

	// No collateralFactorMantissa may exceed this value
	uint256 internal constant collateralFactorMaxMantissa = 0.9e18; // 0.9

	// liquidationIncentiveMantissa must be no less than this value
	uint256 internal constant liquidationIncentiveMinMantissa = 1.0e18; // 1.0

	// liquidationIncentiveMantissa must be no greater than this value
	uint256 internal constant liquidationIncentiveMaxMantissa = 1.5e18; // 1.5

	modifier onlyAdminOrSuperior {
		require(admin == msg.sender || superior == msg.sender, "The caller must be a admin or superior");
		_;
	}

	function initialize(
		address _admin,
		address _superior,
		address _oracle
	) external override {
		admin = _admin;
		superior = _superior;
		oracle = PriceOracleInterface(_oracle);

		closeFactorMantissa = 0.5e18; // 0.5
		liquidationIncentiveMantissa = 1.08e18; // 1.08
		defaultCollateralFactorMantissa = 0.75e18; // 0.75
		maxAssets = 256;
	}

	/*** Assets You Are In ***/

	/**
	 * @notice Returns the assets an account has entered
	 * @param account The address of the account to pull assets for
	 * @return A dynamic list with the assets the account has entered
	 */
	function getAssetsIn(address account) external view returns (HToken[] memory) {
		HToken[] memory assetsIn = accountAssets[account];

		return assetsIn;
	}

	/**
	 * @notice Return all of the markets
	 * @dev The automatic getter may be used to access an individual market.
	 * @return The list of market addresses
	 */
	function getAllMarkets() external view returns (HToken[] memory) {
		return allMarkets;
	}

	/**
	 * @notice Returns whether the given account is entered in the given asset
	 * @param account The address of the account to check
	 * @param hToken The hToken to check
	 * @return True if the account is in the asset, otherwise false.
	 */
	function checkMembership(address account, HToken hToken) external view returns (bool) {
		return markets[address(hToken)].accountMembership[account];
	}

	/**
	 * @notice Add assets to be included in account liquidity calculation
	 * @param hTokens The list of addresses of the hToken markets to be enabled
	 * @return Success indicator for whether each corresponding market was entered
	 */
	function enterMarkets(address[] calldata hTokens) external override returns (uint256[] memory) {
		uint256 len = hTokens.length;

		uint256[] memory results = new uint256[](len);
		for (uint256 i = 0; i < len; i++) {
			HToken hToken = HToken(hTokens[i]);

			results[i] = uint256(addToMarketInternal(hToken, msg.sender));
		}

		return results;
	}

	/**
	 * @notice Add the market to the borrower's "assets in" for liquidity calculations
	 * @param hToken The market to enter
	 * @param borrower The address of the account to modify
	 * @return Success indicator for whether the market was entered
	 */
	function addToMarketInternal(HToken hToken, address borrower) internal returns (Error) {
		Market storage marketToJoin = markets[address(hToken)];

		if (!marketToJoin.isListed) {
			// market is not listed, cannot join
			return Error.MARKET_NOT_LISTED;
		}

		if (marketToJoin.accountMembership[borrower] == true) {
			// already joined
			return Error.NO_ERROR;
		}

		if (accountAssets[borrower].length >= maxAssets) {
			// no space, cannot join
			return Error.TOO_MANY_ASSETS;
		}

		// survived the gauntlet, add to list
		// NOTE: we store these somewhat redundantly as a significant optimization
		//  this avoids having to iterate through the list for the most common use cases
		//  that is, only when we need to perform liquidity checks
		//  and not whenever we want to check if an account is in a particular market
		marketToJoin.accountMembership[borrower] = true;
		accountAssets[borrower].push(HToken(hToken));

		emit MarketEntered(address(hToken), borrower);

		return Error.NO_ERROR;
	}

	/**
	 * @notice Removes asset from sender's account liquidity calculation
	 * @dev Sender must not have an outstanding borrow balance in the asset,
	 *  or be providing neccessary collateral for an outstanding borrow.
	 * @param hTokenAddress The address of the asset to be removed
	 * @return Whether or not the account successfully exited the market
	 */
	function exitMarket(address hTokenAddress) external override returns (uint256) {
		HToken hToken = HToken(hTokenAddress);
		/* Get sender tokensHeld and amountOwed underlying from the hToken */
		(uint256 oErr, uint256 tokensHeld, uint256 amountOwed, ) = hToken.getAccountSnapshot(msg.sender);
		require(oErr == 0, "exitMarket: getAccountSnapshot failed"); // semi-opaque error code

		/* Fail if the sender has a borrow balance */
		if (amountOwed != 0) {
			return fail(Error.NONZERO_BORROW_BALANCE, FailureInfo.EXIT_MARKET_BALANCE_OWED);
		}

		/* Fail if the sender is not permitted to redeem all of their tokens */
		uint256 allowed = redeemAllowedInternal(hTokenAddress, msg.sender, tokensHeld);
		if (allowed != 0) {
			return failOpaque(Error.REJECTION, FailureInfo.EXIT_MARKET_REJECTION, allowed);
		}

		Market storage marketToExit = markets[hTokenAddress];

		/* Return true if the sender is not already ‘in’ the market */
		if (!marketToExit.accountMembership[msg.sender]) {
			return uint256(Error.NO_ERROR);
		}

		/* Set hToken account membership to false */
		delete marketToExit.accountMembership[msg.sender];

		/* Delete hToken from the account’s list of assets */
		// load into memory for faster iteration
		HToken[] memory userAssetList = accountAssets[msg.sender];
		uint256 len = userAssetList.length;
		uint256 assetIndex = len;
		for (uint256 i = 0; i < len; i++) {
			if (userAssetList[i] == hToken) {
				assetIndex = i;
				break;
			}
		}

		// We *must* have found the asset in the list or our redundant data structure is broken
		assert(assetIndex < len);

		// copy last item in list to location of item to be removed, reduce length by 1
		HToken[] storage storedList = accountAssets[msg.sender];
		storedList[assetIndex] = storedList[storedList.length - 1];
		storedList.pop();

		emit MarketExited(hTokenAddress, msg.sender);

		return uint256(Error.NO_ERROR);
	}

	function distributeAllowed(address hToken) external view returns (bool) {
		return markets[hToken].isListed;
	}

	/*** Policy Hooks ***/

	/**
	 * @notice Checks if the account should be allowed to mint tokens in the given market
	 * @param hToken The market to verify the mint against
	 * @param minter The account which would get the minted tokens
	 * @param mintAmount The amount of underlying being supplied to the market in exchange for tokens
	 * @return 0 if the mint is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
	 */
	function mintAllowed(
		address hToken,
		address minter,
		uint256 mintAmount
	) external override returns (uint256) {
		// Pausing is a very serious situation - we revert to sound the alarms
		require(!mintGuardianPaused[hToken], "mint is paused");

		// Shh - currently unused
		minter;
		mintAmount;

		if (!markets[hToken].isListed) {
			return uint256(Error.MARKET_NOT_LISTED);
		}

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Validates mint and reverts on rejection. May emit logs.
	 * @param hToken Asset being minted
	 * @param minter The address minting the tokens
	 * @param actualMintAmount The amount of the underlying asset being minted
	 * @param mintTokens The number of tokens being minted
	 */
	function mintVerify(
		address hToken,
		address minter,
		uint256 actualMintAmount,
		uint256 mintTokens
	) external override {
		// Shh - currently unused
		hToken;
		minter;
		actualMintAmount;
		mintTokens;

		// Shh - we don't ever want this hook to be marked pure
		if (false) {
			maxAssets = maxAssets;
		}
	}

	/**
	 * @notice Checks if the account should be allowed to redeem tokens in the given market
	 * @param hToken The market to verify the redeem against
	 * @param redeemer The account which would redeem the tokens
	 * @param redeemTokens The number of hTokens to exchange for the underlying asset in the market
	 * @return 0 if the redeem is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
	 */
	function redeemAllowed(
		address hToken,
		address redeemer,
		uint256 redeemTokens
	) external override returns (uint256) {
		uint256 allowed = redeemAllowedInternal(hToken, redeemer, redeemTokens);
		if (allowed != uint256(Error.NO_ERROR)) {
			return allowed;
		}

		return uint256(Error.NO_ERROR);
	}

	function redeemAllowedInternal(
		address hToken,
		address redeemer,
		uint256 redeemTokens
	) internal view returns (uint256) {
		if (!markets[hToken].isListed) {
			return uint256(Error.MARKET_NOT_LISTED);
		}

		/* If the redeemer is not 'in' the market, then we can bypass the liquidity check */
		if (!markets[hToken].accountMembership[redeemer]) {
			return uint256(Error.NO_ERROR);
		}

		/* Otherwise, perform a hypothetical liquidity check to guard against shortfall */
		(Error err, , uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
			redeemer,
			HToken(hToken),
			redeemTokens,
			0
		);
		if (err != Error.NO_ERROR) {
			return uint256(err);
		}
		if (shortfall > 0) {
			return uint256(Error.INSUFFICIENT_LIQUIDITY);
		}

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Validates redeem and reverts on rejection. May emit logs.
	 * @param hToken Asset being redeemed
	 * @param redeemer The address redeeming the tokens
	 * @param redeemAmount The amount of the underlying asset being redeemed
	 * @param redeemTokens The number of tokens being redeemed
	 */
	function redeemVerify(
		address hToken,
		address redeemer,
		uint256 redeemAmount,
		uint256 redeemTokens
	) external override {
		// Shh - currently unused
		hToken;
		redeemer;

		// Require tokens is zero or amount is also zero
		if (redeemTokens == 0 && redeemAmount > 0) {
			revert("redeemTokens zero");
		}
	}

	/**
	 * @notice Checks if the account should be allowed to borrow the underlying asset of the given market
	 * @param hToken The market to verify the borrow against
	 * @param borrower The account which would borrow the asset
	 * @param borrowAmount The amount of underlying the account would borrow
	 * @return 0 if the borrow is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
	 */
	function borrowAllowed(
		address hToken,
		address borrower,
		uint256 borrowAmount
	) external override returns (uint256) {
		// Pausing is a very serious situation - we revert to sound the alarms
		require(!borrowGuardianPaused[hToken], "borrow is paused");

		if (!markets[hToken].isListed) {
			return uint256(Error.MARKET_NOT_LISTED);
		}

		if (!markets[hToken].accountMembership[borrower]) {
			// only hTokens may call borrowAllowed if borrower not in market
			require(msg.sender == hToken, "sender must be hToken");

			// attempt to add borrower to the market
			Error err = addToMarketInternal(HToken(msg.sender), borrower);
			if (err != Error.NO_ERROR) {
				return uint256(err);
			}

			// it should be impossible to break the important invariant
			assert(markets[hToken].accountMembership[borrower]);
		}

		if (oracle.getUnderlyingPrice(hToken) == 0) {
			return uint256(Error.PRICE_ERROR);
		}

		(Error err, , uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
			borrower,
			HToken(hToken),
			0,
			borrowAmount
		);
		if (err != Error.NO_ERROR) {
			return uint256(err);
		}
		if (shortfall > 0) {
			return uint256(Error.INSUFFICIENT_LIQUIDITY);
		}

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Validates borrow and reverts on rejection. May emit logs.
	 * @param hToken Asset whose underlying is being borrowed
	 * @param borrower The address borrowing the underlying
	 * @param borrowAmount The amount of the underlying asset requested to borrow
	 */
	function borrowVerify(
		address hToken,
		address borrower,
		uint256 borrowAmount
	) external override {
		// Shh - currently unused
		hToken;
		borrower;
		borrowAmount;

		// Shh - we don't ever want this hook to be marked pure
		if (false) {
			maxAssets = maxAssets;
		}
	}

	/**
	 * @notice Checks if the account should be allowed to repay a borrow in the given market
	 * @param hToken The market to verify the repay against
	 * @param payer The account which would repay the asset
	 * @param borrower The account which would borrowed the asset
	 * @param repayAmount The amount of the underlying asset the account would repay
	 * @return 0 if the repay is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
	 */
	function repayBorrowAllowed(
		address hToken,
		address payer,
		address borrower,
		uint256 repayAmount
	) external override returns (uint256) {
		// Shh - currently unused
		payer;
		borrower;
		repayAmount;

		if (!markets[hToken].isListed) {
			return uint256(Error.MARKET_NOT_LISTED);
		}

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Validates repayBorrow and reverts on rejection. May emit logs.
	 * @param hToken Asset being repaid
	 * @param payer The address repaying the borrow
	 * @param borrower The address of the borrower
	 * @param actualRepayAmount The amount of underlying being repaid
	 */
	function repayBorrowVerify(
		address hToken,
		address payer,
		address borrower,
		uint256 actualRepayAmount,
		uint256 borrowerIndex
	) external override {
		// Shh - currently unused
		hToken;
		payer;
		borrower;
		actualRepayAmount;
		borrowerIndex;

		// Shh - we don't ever want this hook to be marked pure
		if (false) {
			maxAssets = maxAssets;
		}
	}

	/**
	 * @notice Checks if the liquidation should be allowed to occur
	 * @param hTokenBorrowed Asset which was borrowed by the borrower
	 * @param hTokenCollateral Asset which was used as collateral and will be seized
	 * @param liquidator The address repaying the borrow and seizing the collateral
	 * @param borrower The address of the borrower
	 * @param repayAmount The amount of underlying being repaid
	 */
	function liquidateBorrowAllowed(
		address hTokenBorrowed,
		address hTokenCollateral,
		address liquidator,
		address borrower,
		uint256 repayAmount
	) external override returns (uint256) {
		// Shh - currently unused
		liquidator;

		if (!markets[hTokenBorrowed].isListed || !markets[hTokenCollateral].isListed) {
			return uint256(Error.MARKET_NOT_LISTED);
		}

		/* The borrower must have shortfall in order to be liquidatable */
		(Error err, , uint256 shortfall) = getAccountLiquidityInternal(borrower);
		if (err != Error.NO_ERROR) {
			return uint256(err);
		}
		if (shortfall == 0) {
			return uint256(Error.INSUFFICIENT_SHORTFALL);
		}

		/* The liquidator may not repay more than what is allowed by the closeFactor */
		uint256 borrowBalance = HToken(hTokenBorrowed).borrowBalanceCurrent(borrower);
		(MathError mathErr, uint256 maxClose) = mulScalarTruncate(Exp({ mantissa: closeFactorMantissa }), borrowBalance);
		if (mathErr != MathError.NO_ERROR) {
			return uint256(Error.MATH_ERROR);
		}
		if (repayAmount > maxClose) {
			return uint256(Error.TOO_MUCH_REPAY);
		}

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Validates liquidateBorrow and reverts on rejection. May emit logs.
	 * @param hTokenBorrowed Asset which was borrowed by the borrower
	 * @param hTokenCollateral Asset which was used as collateral and will be seized
	 * @param liquidator The address repaying the borrow and seizing the collateral
	 * @param borrower The address of the borrower
	 * @param actualRepayAmount The amount of underlying being repaid
	 */
	function liquidateBorrowVerify(
		address hTokenBorrowed,
		address hTokenCollateral,
		address liquidator,
		address borrower,
		uint256 actualRepayAmount,
		uint256 seizeTokens
	) external override {
		// Shh - currently unused
		hTokenBorrowed;
		hTokenCollateral;
		liquidator;
		borrower;
		actualRepayAmount;
		seizeTokens;

		// Shh - we don't ever want this hook to be marked pure
		if (false) {
			maxAssets = maxAssets;
		}
	}

	/**
	 * @notice Checks if the seizing of assets should be allowed to occur
	 * @param hTokenCollateral Asset which was used as collateral and will be seized
	 * @param hTokenBorrowed Asset which was borrowed by the borrower
	 * @param liquidator The address repaying the borrow and seizing the collateral
	 * @param borrower The address of the borrower
	 * @param seizeTokens The number of collateral tokens to seize
	 */
	function seizeAllowed(
		address hTokenCollateral,
		address hTokenBorrowed,
		address liquidator,
		address borrower,
		uint256 seizeTokens
	) external override returns (uint256) {
		// Pausing is a very serious situation - we revert to sound the alarms
		require(!seizeGuardianPaused, "seize is paused");

		// Shh - currently unused
		seizeTokens;
		liquidator;
		borrower;

		if (!markets[hTokenCollateral].isListed || !markets[hTokenBorrowed].isListed) {
			return uint256(Error.MARKET_NOT_LISTED);
		}

		if (HToken(hTokenCollateral).controller() != HToken(hTokenBorrowed).controller()) {
			return uint256(Error.CONTROLLER_MISMATCH);
		}

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Validates seize and reverts on rejection. May emit logs.
	 * @param hTokenCollateral Asset which was used as collateral and will be seized
	 * @param hTokenBorrowed Asset which was borrowed by the borrower
	 * @param liquidator The address repaying the borrow and seizing the collateral
	 * @param borrower The address of the borrower
	 * @param seizeTokens The number of collateral tokens to seize
	 */
	function seizeVerify(
		address hTokenCollateral,
		address hTokenBorrowed,
		address liquidator,
		address borrower,
		uint256 seizeTokens
	) external override {
		// Shh - currently unused
		hTokenCollateral;
		hTokenBorrowed;
		liquidator;
		borrower;
		seizeTokens;

		// Shh - we don't ever want this hook to be marked pure
		if (false) {
			maxAssets = maxAssets;
		}
	}

	/**
	 * @notice Checks if the account should be allowed to transfer tokens in the given market
	 * @param hToken The market to verify the transfer against
	 * @param src The account which sources the tokens
	 * @param dst The account which receives the tokens
	 * @param transferTokens The number of hTokens to transfer
	 * @return 0 if the transfer is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
	 */
	function transferAllowed(
		address hToken,
		address src,
		address dst,
		uint256 transferTokens
	) external override returns (uint256) {
		// Pausing is a very serious situation - we revert to sound the alarms
		require(!transferGuardianPaused, "transfer is paused");

		// Currently the only consideration is whether or not
		//  the src is allowed to redeem this many tokens
		uint256 allowed = redeemAllowedInternal(hToken, src, transferTokens);
		if (allowed != uint256(Error.NO_ERROR)) {
			return allowed;
		}

		dst;

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Validates transfer and reverts on rejection. May emit logs.
	 * @param hToken Asset being transferred
	 * @param src The account which sources the tokens
	 * @param dst The account which receives the tokens
	 * @param transferTokens The number of hTokens to transfer
	 */
	function transferVerify(
		address hToken,
		address src,
		address dst,
		uint256 transferTokens
	) external override {
		// Shh - currently unused
		hToken;
		src;
		dst;
		transferTokens;

		// Shh - we don't ever want this hook to be marked pure
		if (false) {
			maxAssets = maxAssets;
		}
	}

	/*** Liquidity/Liquidation Calculations ***/

	/**
	 * @dev Local vars for avoiding stack-depth limits in calculating account liquidity.
	 *  Note that `hTokenBalance` is the number of hTokens the account owns in the market,
	 *  whereas `borrowBalance` is the amount of underlying that the account has borrowed.
	 */
	struct AccountLiquidityLocalVars {
		uint256 sumCollateral;
		uint256 sumBorrowPlusEffects;
		uint256 hTokenBalance;
		uint256 borrowBalance;
		uint256 exchangeRateMantissa;
		uint256 oraclePriceMantissa;
		Exp collateralFactor;
		Exp exchangeRate;
		Exp oraclePrice;
		Exp tokensToDenom;
	}

	/**
     * @notice Determine the current account liquidity wrt collateral requirements
     * @return (possible error code (semi-opaque),
                account liquidity in excess of collateral requirements,
     *          account shortfall below collateral requirements)
     */
	function getAccountLiquidity(address account)
		public
		view
		returns (
			uint256,
			uint256,
			uint256
		)
	{
		(Error err, uint256 liquidity, uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
			account,
			HToken(0),
			0,
			0
		);

		return (uint256(err), liquidity, shortfall);
	}

	/**
     * @notice Determine the current account liquidity wrt collateral requirements
     * @return (possible error code,
                account liquidity in excess of collateral requirements,
     *          account shortfall below collateral requirements)
     */
	function getAccountLiquidityInternal(address account)
		internal
		view
		returns (
			Error,
			uint256,
			uint256
		)
	{
		return getHypotheticalAccountLiquidityInternal(account, HToken(0), 0, 0);
	}

	/**
     * @notice Determine what the account liquidity would be if the given amounts were redeemed/borrowed
     * @param hTokenModify The market to hypothetically redeem/borrow in
     * @param account The account to determine liquidity for
     * @param redeemTokens The number of tokens to hypothetically redeem
     * @param borrowAmount The amount of underlying to hypothetically borrow
     * @return (possible error code (semi-opaque),
                hypothetical account liquidity in excess of collateral requirements,
     *          hypothetical account shortfall below collateral requirements)
     */
	function getHypotheticalAccountLiquidity(
		address account,
		address hTokenModify,
		uint256 redeemTokens,
		uint256 borrowAmount
	)
		public
		view
		returns (
			uint256,
			uint256,
			uint256
		)
	{
		(Error err, uint256 liquidity, uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
			account,
			HToken(hTokenModify),
			redeemTokens,
			borrowAmount
		);
		return (uint256(err), liquidity, shortfall);
	}

	/**
     * @notice Determine what the account liquidity would be if the given amounts were redeemed/borrowed
     * @param hTokenModify The market to hypothetically redeem/borrow in
     * @param account The account to determine liquidity for
     * @param redeemTokens The number of tokens to hypothetically redeem
     * @param borrowAmount The amount of underlying to hypothetically borrow
     * @dev Note that we calculate the exchangeRateStored for each collateral hToken using stored data,
     *  without calculating accumulated interest.
     * @return (possible error code,
                hypothetical account liquidity in excess of collateral requirements,
     *          hypothetical account shortfall below collateral requirements)
     */
	function getHypotheticalAccountLiquidityInternal(
		address account,
		HToken hTokenModify,
		uint256 redeemTokens,
		uint256 borrowAmount
	)
		internal
		view
		returns (
			Error,
			uint256,
			uint256
		)
	{
		AccountLiquidityLocalVars memory vars; // Holds all our calculation results
		uint256 oErr;
		MathError mErr;

		// For each asset the account is in
		HToken[] memory assets = accountAssets[account];
		for (uint256 i = 0; i < assets.length; i++) {
			HToken asset = assets[i];

			// Read the balances and exchange rate from the hToken
			(oErr, vars.hTokenBalance, vars.borrowBalance, vars.exchangeRateMantissa) = asset.getAccountSnapshot(account);
			if (oErr != 0) {
				// semi-opaque error code, we assume NO_ERROR == 0 is invariant between upgrades
				return (Error.SNAPSHOT_ERROR, 0, 0);
			}
			vars.collateralFactor = Exp({ mantissa: markets[address(asset)].collateralFactorMantissa });
			vars.exchangeRate = Exp({ mantissa: vars.exchangeRateMantissa });

			// Get the normalized price of the asset
			vars.oraclePriceMantissa = oracle.getUnderlyingPrice(address(asset));
			if (vars.oraclePriceMantissa == 0) {
				return (Error.PRICE_ERROR, 0, 0);
			}
			vars.oraclePrice = Exp({ mantissa: vars.oraclePriceMantissa });

			// Pre-compute a conversion factor from tokens -> ether (normalized price value)
			(mErr, vars.tokensToDenom) = mulExp3(vars.collateralFactor, vars.exchangeRate, vars.oraclePrice);
			if (mErr != MathError.NO_ERROR) {
				return (Error.MATH_ERROR, 0, 0);
			}

			// sumCollateral += tokensToDenom * hTokenBalance
			(mErr, vars.sumCollateral) = mulScalarTruncateAddUInt(vars.tokensToDenom, vars.hTokenBalance, vars.sumCollateral);
			if (mErr != MathError.NO_ERROR) {
				return (Error.MATH_ERROR, 0, 0);
			}

			// sumBorrowPlusEffects += oraclePrice * borrowBalance
			(mErr, vars.sumBorrowPlusEffects) = mulScalarTruncateAddUInt(
				vars.oraclePrice,
				vars.borrowBalance,
				vars.sumBorrowPlusEffects
			);
			if (mErr != MathError.NO_ERROR) {
				return (Error.MATH_ERROR, 0, 0);
			}

			// Calculate effects of interacting with hTokenModify
			if (asset == hTokenModify) {
				// redeem effect
				// sumBorrowPlusEffects += tokensToDenom * redeemTokens
				(mErr, vars.sumBorrowPlusEffects) = mulScalarTruncateAddUInt(
					vars.tokensToDenom,
					redeemTokens,
					vars.sumBorrowPlusEffects
				);
				if (mErr != MathError.NO_ERROR) {
					return (Error.MATH_ERROR, 0, 0);
				}

				// borrow effect
				// sumBorrowPlusEffects += oraclePrice * borrowAmount
				(mErr, vars.sumBorrowPlusEffects) = mulScalarTruncateAddUInt(
					vars.oraclePrice,
					borrowAmount,
					vars.sumBorrowPlusEffects
				);
				if (mErr != MathError.NO_ERROR) {
					return (Error.MATH_ERROR, 0, 0);
				}
			}
		}

		// These are safe, as the underflow condition is checked first
		if (vars.sumCollateral > vars.sumBorrowPlusEffects) {
			return (Error.NO_ERROR, vars.sumCollateral - vars.sumBorrowPlusEffects, 0);
		} else {
			return (Error.NO_ERROR, 0, vars.sumBorrowPlusEffects - vars.sumCollateral);
		}
	}

	/**
	 * @notice Calculate number of tokens of collateral asset to seize given an underlying amount
	 * @dev Used in liquidation (called in hToken.liquidateBorrowFresh)
	 * @param hTokenBorrowed The address of the borrowed hToken
	 * @param hTokenCollateral The address of the collateral hToken
	 * @param actualRepayAmount The amount of hTokenBorrowed underlying to convert into hTokenCollateral tokens
	 * @return (errorCode, number of hTokenCollateral tokens to be seized in a liquidation)
	 */
	function liquidateCalculateSeizeTokens(
		address hTokenBorrowed,
		address hTokenCollateral,
		uint256 actualRepayAmount
	) external override view returns (uint256, uint256) {
		/* Read oracle prices for borrowed and collateral markets */
		uint256 priceBorrowedMantissa = oracle.getUnderlyingPrice(hTokenBorrowed);
		uint256 priceCollateralMantissa = oracle.getUnderlyingPrice(hTokenCollateral);
		if (priceBorrowedMantissa == 0 || priceCollateralMantissa == 0) {
			return (uint256(Error.PRICE_ERROR), 0);
		}

		/*
		 * Get the exchange rate and calculate the number of collateral tokens to seize:
		 *  seizeAmount = actualRepayAmount * liquidationIncentive * priceBorrowed / priceCollateral
		 *  seizeTokens = seizeAmount / exchangeRate
		 *   = actualRepayAmount * (liquidationIncentive * priceBorrowed) / (priceCollateral * exchangeRate)
		 */
		uint256 exchangeRateMantissa = HToken(hTokenCollateral).exchangeRateCurrent();
		uint256 seizeTokens;
		Exp memory numerator;
		Exp memory denominator;
		Exp memory ratio;
		MathError mathErr;

		(mathErr, numerator) = mulExp(liquidationIncentiveMantissa, priceBorrowedMantissa);
		if (mathErr != MathError.NO_ERROR) {
			return (uint256(Error.MATH_ERROR), 0);
		}

		(mathErr, denominator) = mulExp(priceCollateralMantissa, exchangeRateMantissa);
		if (mathErr != MathError.NO_ERROR) {
			return (uint256(Error.MATH_ERROR), 0);
		}

		(mathErr, ratio) = divExp(numerator, denominator);
		if (mathErr != MathError.NO_ERROR) {
			return (uint256(Error.MATH_ERROR), 0);
		}

		(mathErr, seizeTokens) = mulScalarTruncate(ratio, actualRepayAmount);
		if (mathErr != MathError.NO_ERROR) {
			return (uint256(Error.MATH_ERROR), 0);
		}

		return (uint256(Error.NO_ERROR), seizeTokens);
	}

	/**
	 * @notice Sets the closeFactor used when liquidating borrows
	 * @dev Admin function to set closeFactor
	 * @param newCloseFactorMantissa New close factor, scaled by 1e18
	 * @return uint 0=success, otherwise a failure. (See ErrorReporter for details)
	 */
	function _setCloseFactor(uint256 newCloseFactorMantissa) external returns (uint256) {
		// Check caller is admin
		if (msg.sender != admin) {
			return fail(Error.UNAUTHORIZED, FailureInfo.SET_CLOSE_FACTOR_OWNER_CHECK);
		}

		Exp memory newCloseFactorExp = Exp({ mantissa: newCloseFactorMantissa });
		Exp memory lowLimit = Exp({ mantissa: closeFactorMinMantissa });
		if (lessThanOrEqualExp(newCloseFactorExp, lowLimit)) {
			return fail(Error.INVALID_CLOSE_FACTOR, FailureInfo.SET_CLOSE_FACTOR_VALIDATION);
		}

		Exp memory highLimit = Exp({ mantissa: closeFactorMaxMantissa });
		if (lessThanExp(highLimit, newCloseFactorExp)) {
			return fail(Error.INVALID_CLOSE_FACTOR, FailureInfo.SET_CLOSE_FACTOR_VALIDATION);
		}

		uint256 oldCloseFactorMantissa = closeFactorMantissa;
		closeFactorMantissa = newCloseFactorMantissa;
		emit NewCloseFactor(oldCloseFactorMantissa, closeFactorMantissa);

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Sets the collateralFactor for a market
	 * @dev Admin function to set per-market collateralFactor
	 * @param hToken The market to set the factor on
	 * @param newCollateralFactorMantissa The new collateral factor, scaled by 1e18
	 * @return uint 0=success, otherwise a failure. (See ErrorReporter for details)
	 */
	function _setCollateralFactor(address hToken, uint256 newCollateralFactorMantissa) external returns (uint256) {
		// Check caller is admin
		if (msg.sender != admin) {
			return fail(Error.UNAUTHORIZED, FailureInfo.SET_COLLATERAL_FACTOR_OWNER_CHECK);
		}

		// Verify market is listed
		Market storage market = markets[hToken];
		if (!market.isListed) {
			return fail(Error.MARKET_NOT_LISTED, FailureInfo.SET_COLLATERAL_FACTOR_NO_EXISTS);
		}

		Exp memory newCollateralFactorExp = Exp({ mantissa: newCollateralFactorMantissa });

		// Check collateral factor <= 0.9
		Exp memory highLimit = Exp({ mantissa: collateralFactorMaxMantissa });
		if (lessThanExp(highLimit, newCollateralFactorExp)) {
			return fail(Error.INVALID_COLLATERAL_FACTOR, FailureInfo.SET_COLLATERAL_FACTOR_VALIDATION);
		}

		// If collateral factor != 0, fail if price == 0
		if (newCollateralFactorMantissa != 0 && oracle.getUnderlyingPrice(hToken) == 0) {
			return fail(Error.PRICE_ERROR, FailureInfo.SET_COLLATERAL_FACTOR_WITHOUT_PRICE);
		}

		// Set market's collateral factor to new collateral factor, remember old value
		uint256 oldCollateralFactorMantissa = market.collateralFactorMantissa;
		market.collateralFactorMantissa = newCollateralFactorMantissa;

		// Emit event with asset, old collateral factor, and new collateral factor
		emit NewCollateralFactor(hToken, oldCollateralFactorMantissa, newCollateralFactorMantissa);

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Sets maxAssets which controls how many markets can be entered
	 * @dev Admin function to set maxAssets
	 * @param newMaxAssets New max assets
	 * @return uint 0=success, otherwise a failure. (See ErrorReporter for details)
	 */
	function _setMaxAssets(uint256 newMaxAssets) external returns (uint256) {
		// Check caller is admin
		if (msg.sender != admin) {
			return fail(Error.UNAUTHORIZED, FailureInfo.SET_MAX_ASSETS_OWNER_CHECK);
		}

		uint256 oldMaxAssets = maxAssets;
		maxAssets = newMaxAssets;
		emit NewMaxAssets(oldMaxAssets, newMaxAssets);

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Sets liquidationIncentive
	 * @dev Admin function to set liquidationIncentive
	 * @param newLiquidationIncentiveMantissa New liquidationIncentive scaled by 1e18
	 * @return uint 0=success, otherwise a failure. (See ErrorReporter for details)
	 */
	function _setLiquidationIncentive(uint256 newLiquidationIncentiveMantissa) external returns (uint256) {
		// Check caller is admin
		if (msg.sender != admin) {
			return fail(Error.UNAUTHORIZED, FailureInfo.SET_LIQUIDATION_INCENTIVE_OWNER_CHECK);
		}

		// Check de-scaled min <= newLiquidationIncentive <= max
		Exp memory newLiquidationIncentive = Exp({ mantissa: newLiquidationIncentiveMantissa });
		Exp memory minLiquidationIncentive = Exp({ mantissa: liquidationIncentiveMinMantissa });
		if (lessThanExp(newLiquidationIncentive, minLiquidationIncentive)) {
			return fail(Error.INVALID_LIQUIDATION_INCENTIVE, FailureInfo.SET_LIQUIDATION_INCENTIVE_VALIDATION);
		}

		Exp memory maxLiquidationIncentive = Exp({ mantissa: liquidationIncentiveMaxMantissa });
		if (lessThanExp(maxLiquidationIncentive, newLiquidationIncentive)) {
			return fail(Error.INVALID_LIQUIDATION_INCENTIVE, FailureInfo.SET_LIQUIDATION_INCENTIVE_VALIDATION);
		}

		// Save current value for use in log
		uint256 oldLiquidationIncentiveMantissa = liquidationIncentiveMantissa;

		// Set liquidation incentive to new incentive
		liquidationIncentiveMantissa = newLiquidationIncentiveMantissa;

		// Emit event with old incentive, new incentive
		emit NewLiquidationIncentive(oldLiquidationIncentiveMantissa, newLiquidationIncentiveMantissa);

		return uint256(Error.NO_ERROR);
	}

	/**
	 * @notice Add the market to the markets mapping and set it as listed
	 * @dev Admin function to set isListed and add support for the market
	 * @param hToken The address of the market (token) to list
	 */
	function _supportMarket(address hToken) external override onlyAdminOrSuperior {
		require(!markets[hToken].isListed, "MarketController::_supportMarket: already listed");

		markets[hToken] = Market({ isListed: true, collateralFactorMantissa: defaultCollateralFactorMantissa });

		allMarkets.push(HToken(HToken(hToken)));

		emit MarketListed(hToken);
	}

	/**
	 * @notice Admin function to change the Pause Guardian
	 * @param newPauseGuardian The address of the new Pause Guardian
	 * @return uint 0=success, otherwise a failure. (See enum Error for details)
	 */
	function _setPauseGuardian(address newPauseGuardian) public returns (uint256) {
		if (msg.sender != admin) {
			return fail(Error.UNAUTHORIZED, FailureInfo.SET_PAUSE_GUARDIAN_OWNER_CHECK);
		}

		// Save current value for inclusion in log
		address oldPauseGuardian = pauseGuardian;

		// Store pauseGuardian with value newPauseGuardian
		pauseGuardian = newPauseGuardian;

		// Emit NewPauseGuardian(OldPauseGuardian, NewPauseGuardian)
		emit NewPauseGuardian(oldPauseGuardian, pauseGuardian);

		return uint256(Error.NO_ERROR);
	}

	function _setMintPaused(address hToken, bool state) public returns (bool) {
		require(markets[hToken].isListed, "cannot pause a market that is not listed");
		require(msg.sender == pauseGuardian || msg.sender == admin, "only pause guardian and admin can pause");
		require(msg.sender == admin || state == true, "only admin can unpause");

		mintGuardianPaused[hToken] = state;
		emit ActionPaused(hToken, "Mint", state);
		return state;
	}

	function _setBorrowPaused(address hToken, bool state) public returns (bool) {
		require(markets[hToken].isListed, "cannot pause a market that is not listed");
		require(msg.sender == pauseGuardian || msg.sender == admin, "only pause guardian and admin can pause");
		require(msg.sender == admin || state == true, "only admin can unpause");

		borrowGuardianPaused[hToken] = state;
		emit ActionPaused(hToken, "Borrow", state);
		return state;
	}

	function _setTransferPaused(bool state) public returns (bool) {
		require(msg.sender == pauseGuardian || msg.sender == admin, "only pause guardian and admin can pause");
		require(msg.sender == admin || state == true, "only admin can unpause");

		transferGuardianPaused = state;
		emit ActionPaused("Transfer", state);
		return state;
	}

	function _setSeizePaused(bool state) public returns (bool) {
		require(msg.sender == pauseGuardian || msg.sender == admin, "only pause guardian and admin can pause");
		require(msg.sender == admin || state == true, "only admin can unpause");

		seizeGuardianPaused = state;
		emit ActionPaused("Seize", state);
		return state;
	}

	function _mintDOL(address _dol, uint256 amount) external onlyAdminOrSuperior {
		DOL dol = DOL(_dol);
		dol.mint(address(this), amount);
	}

	function _burnDOL(address _dol, uint256 amount) external onlyAdminOrSuperior {
		DOL dol = DOL(_dol);
		dol.burn(address(this), amount);
	}

	function _expandDOLSupply(address _hDol, uint256 amount) external onlyAdminOrSuperior {
		HErc20 hDOl = HErc20(_hDol);
		DOL dol = DOL(hDOl.underlying());
		dol.approve(_hDol, uint256(-1));
		uint256 err = hDOl._mintDOL(amount);
		require(err == 0, "MarketController::_mintDOL: HErc20::_mintDOL error");
	}

	function _reduceDOLSupply(address _hDol, uint256 amount) external onlyAdminOrSuperior {
		uint256 err = HErc20(_hDol)._redeemDOL(amount);
		require(err == 0, "MarketController::_mintDOL: HErc20::_reedemDOL error");
	}
}

