pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "./interfaces/DistributorInterface.sol";
import "./interfaces/EIP20NonStandardInterface.sol";
import "./interfaces/PriceOracleInterface.sol";
import "./libraries/SafeMath.sol";
import "./libraries/Exponential.sol";
import "./HDS.sol";

interface Superior {
	function distributeAllowed(address hToken) external returns (bool);
}

contract HDSDistributor is Exponential, DistributorInterface {
	using SafeMath for uint256;

	HDS public hds;
	address public admin;
	PriceOracleInterface public oracle;
	Superior public superior;
	uint256 public rewardsPerBlock;
	uint256 public halvingStartBlock;
	uint256 public nextHalvingBlock;

	TokenPool private mainPool;
	LiquidityProviderPool[] private lpPools;

	int32 internal constant MAIN_POOL_ID = -1;
	uint256 internal constant BLOCKS_PER_YEAR = 2102400;

	modifier onlyAdmin {
		require(admin == msg.sender, "The caller must be admin");
		_;
	}

	modifier distributeAllowed {
		require(superior.distributeAllowed(msg.sender), "Distribute not allowed");
		_;
	}

	function initialize(
		address _admin,
		address _hds,
		address _oracle,
		address _superior
	) external override {
		admin = _admin;
		hds = HDS(_hds);
		oracle = PriceOracleInterface(_oracle);
		superior = Superior(_superior);

		uint256 decimals = hds.decimals();
		rewardsPerBlock = 3 * (10**decimals);
		halvingStartBlock = getCurrentBlockNumber();
		nextHalvingBlock = halvingStartBlock.add(BLOCKS_PER_YEAR);
	}

	function increaseSupply(
		address account,
		address hToken,
		uint256 amount
	) external override distributeAllowed returns (uint256) {
		uint256 priceMantissa = oracle.getUnderlyingPrice(hToken);
		(MathError err, uint256 value) = mulScalarTruncate(Exp({ mantissa: priceMantissa }), amount);
		if (err != MathError.NO_ERROR) {
			return uint256(err);
		}
		uint256 power = valueToPower(value);
		if (power == 0) {
			return uint256(MathError.NO_ERROR);
		}
		updatePower(mainPool, 0, account, int256(power));
		return uint256(MathError.NO_ERROR);
	}

	function decreaseSupply(
		address account,
		address hToken,
		uint256 amount,
		uint256 left
	) external override distributeAllowed returns (uint256) {
		uint256 priceMantissa = oracle.getUnderlyingPrice(hToken);
		(MathError err, uint256 value) = mulScalarTruncate(Exp({ mantissa: priceMantissa }), amount);
		if (err != MathError.NO_ERROR) {
			return uint256(err);
		}
		uint256 power = valueToPower(value);
		if (power == 0) {
			return uint256(MathError.NO_ERROR);
		}
		uint256 accountPower = mainPool.accountRecords[account].power;
		if (power > accountPower) {
			if (left > 0) {
				(err, value) = mulScalarTruncate(Exp({ mantissa: priceMantissa }), left);
				if (err != MathError.NO_ERROR) {
					return uint256(err);
				}
				power = valueToPower(value);
				if (power == 0) {
					return uint256(MathError.NO_ERROR);
				}
			} else {
				require(left == 0, "left balance shoud be zero");
				power = accountPower;
			}
		}
		updatePower(mainPool, 0, account, -1 * int256(power));
		return uint256(MathError.NO_ERROR);
	}

	function increaseBorrow(
		address account,
		address hToken,
		uint256 amount
	) external override distributeAllowed returns (uint256) {
		uint256 priceMantissa = oracle.getUnderlyingPrice(hToken);
		(MathError err, uint256 value) = mulScalarTruncate(Exp({ mantissa: priceMantissa }), amount);
		if (err != MathError.NO_ERROR) {
			return uint256(err);
		}
		uint256 power = valueToBorrowPower(value);
		if (power == 0) {
			return uint256(MathError.NO_ERROR);
		}
		updatePower(mainPool, 0, account, int256(power));
		return uint256(MathError.NO_ERROR);
	}

	function decreaseBorrow(
		address account,
		address hToken,
		uint256 amount,
		uint256 left
	) external override distributeAllowed returns (uint256) {
		uint256 priceMantissa = oracle.getUnderlyingPrice(hToken);
		(MathError err, uint256 value) = mulScalarTruncate(Exp({ mantissa: priceMantissa }), amount);
		if (err != MathError.NO_ERROR) {
			return uint256(err);
		}
		uint256 power = valueToBorrowPower(value);
		if (power == 0) {
			return uint256(MathError.NO_ERROR);
		}
		uint256 accountPower = mainPool.accountRecords[account].power;
		if (power > accountPower) {
			if (left > 0) {
				(err, value) = mulScalarTruncate(Exp({ mantissa: priceMantissa }), left);
				if (err != MathError.NO_ERROR) {
					return uint256(err);
				}
				power = valueToBorrowPower(value);
				if (power == 0) {
					return uint256(MathError.NO_ERROR);
				}
			} else {
				require(left == 0, "left balance shoud be zero");
				power = accountPower;
			}
		}
		updatePower(mainPool, 0, account, -1 * int256(power));
		return uint256(MathError.NO_ERROR);
	}

	function createLPPool(
		address lpToken,
		uint256 startBlock,
		uint256 endBlock,
		uint256 tokensPerBlock
	) external override onlyAdmin returns (uint32) {
		uint32 id = uint32(lpPools.length);
		LiquidityProviderPool memory newPool;
		newPool.id = id;
		newPool.lpToken = lpToken;
		newPool.endBlock = endBlock;
		newPool.startBlock = startBlock;
		newPool.tokensPerBlock = tokensPerBlock;
		lpPools.push(newPool);
		return id;
	}

	function mintLPPool(uint32 id, uint256 amount) external override {
		if (id >= lpPools.length) {
			return;
		}
		LiquidityProviderPool storage pool = lpPools[id];
		uint256 currentBlockNumber = getCurrentBlockNumber();
		if (pool.startBlock > currentBlockNumber || pool.endBlock <= currentBlockNumber) {
			return;
		}
		uint256 transferedAmount = doTransferIn(pool.lpToken, msg.sender, amount);
		updatePower(pool.base, int32(id), msg.sender, int256(transferedAmount));
	}

	function claimMainPool() external override returns (uint256) {
		AccountRecord storage record = mainPool.accountRecords[msg.sender];
		uint256 rewardIndex = mainPool.rewardIndex;
		uint256 total = rewardIndex.mul(record.power).add(record.settled).sub(record.mask);
		hds.mint(msg.sender, total);
		record.claimed = record.claimed.add(total);
		record.settled = 0;
		record.mask = rewardIndex.mul(record.power);
		return total;
	}

	function claimLPPool(uint32 id) external override returns (uint256) {
		return claimLPPoolInternal(id);
	}

	function exitLPPool(uint32 id, bool isClaim) external override {
		if (id >= lpPools.length) {
			return;
		}
		LiquidityProviderPool storage pool = lpPools[id];
		uint256 amount = pool.base.accountRecords[msg.sender].power;
		if (amount > 0) {
			doTransferOut(pool.lpToken, msg.sender, amount);
		}
		if (isClaim) {
			claimLPPoolInternal(id);
		}
	}

	function getMainPoolMetadata() external override view returns (TokenPoolMeta memory) {
		return
			TokenPoolMeta({
				rewardIndex: mainPool.rewardIndex,
				lastBlockNumber: mainPool.lastBlockNumber,
				accumulatedPower: mainPool.accumulatedPower,
				accumulatedTokens: mainPool.accumulatedTokens,
				totalPower: mainPool.totalPower
			});
	}

	function getAllLPPools() external override view returns (LiquidityProviderPoolMeta[] memory) {
		LiquidityProviderPoolMeta[] memory results = new LiquidityProviderPoolMeta[](lpPools.length);
		for (uint256 i; i < results.length; i++) {
			LiquidityProviderPool memory pool = lpPools[i];
			TokenPool memory base = pool.base;
			results[i] = LiquidityProviderPoolMeta({
				id: pool.id,
				lpToken: pool.lpToken,
				startBlock: pool.startBlock,
				endBlock: pool.endBlock,
				tokensPerBlock: pool.tokensPerBlock,
				rewardIndex: base.rewardIndex,
				lastBlockNumber: base.lastBlockNumber,
				accumulatedPower: base.accumulatedPower,
				accumulatedTokens: base.accumulatedTokens,
				totalPower: base.totalPower
			});
		}
		return results;
	}

	function getAccountRecordInPool(address account, int32 pool) external override view returns (AccountRecord memory) {
		if (pool == -1) {
			return mainPool.accountRecords[account];
		}
		uint32 id = uint32(pool);
		require(id < lpPools.length, "pool id out of range");
		return lpPools[id].base.accountRecords[account];
	}

	function updatePower(
		TokenPool storage pool,
		int32 poolId,
		address account,
		int256 power
	) internal {
		uint256 currentBlockNumber = getCurrentBlockNumber();

		if (pool.lastBlockNumber != currentBlockNumber) {
			tryHalving();
			pool.totalPower = safeAddUnsignedSigned(pool.totalPower, pool.accumulatedPower);
			uint256 newMinedTokens;
			if (poolId == MAIN_POOL_ID) {
				// Main pool
				newMinedTokens = tokensMinedMainPool(pool.lastBlockNumber, currentBlockNumber);
			} else {
				// LP pool
				LiquidityProviderPool memory lpPool = lpPools[uint256(poolId)];
				require(lpPool.lpToken != address(0), "LP pool not exist");
				newMinedTokens = lpPool.tokensPerBlock.mul(currentBlockNumber - pool.lastBlockNumber);
			}
			if (pool.totalPower > 0) {
				uint256 totalTokens = pool.accumulatedTokens.add(newMinedTokens);
				pool.rewardIndex = pool.rewardIndex.add(totalTokens.div(pool.totalPower));
				pool.accumulatedTokens = 0;
			} else {
				pool.accumulatedTokens = pool.accumulatedTokens.add(newMinedTokens);
			}
			pool.lastBlockNumber = currentBlockNumber;
			pool.accumulatedPower = power;
		} else {
			pool.accumulatedPower = safeAddSigned(pool.accumulatedPower, power);
		}

		AccountRecord storage record = pool.accountRecords[account];
		if (power < 0) {
			record.settled = record.settled.add(pool.rewardIndex.mul(record.power).sub(record.mask));
			record.power = safeAddUnsignedSigned(record.power, power);
			record.mask = pool.rewardIndex.mul(record.power);
		} else {
			record.power = record.power.add(uint256(power));
			record.mask = record.mask.add(pool.rewardIndex.mul(uint256(power)));
		}
	}

	function claimLPPoolInternal(uint32 id) internal returns (uint256) {
		if (id >= lpPools.length) {
			return 0;
		}
		LiquidityProviderPool storage pool = lpPools[id];
		AccountRecord storage record = pool.base.accountRecords[msg.sender];
		uint256 rewardIndex = pool.base.rewardIndex;
		uint256 total = rewardIndex.mul(record.power).add(record.settled).sub(record.mask);
		hds.mint(msg.sender, total);
		record.claimed = record.claimed.add(total);
		record.settled = 0;
		record.mask = rewardIndex.mul(record.power);
		return total;
	}

	function doTransferIn(
		address addr,
		address from,
		uint256 amount
	) internal returns (uint256) {
		EIP20NonStandardInterface token = EIP20NonStandardInterface(addr);
		uint256 balanceBefore = token.balanceOf(address(this));
		token.transferFrom(from, address(this), amount);

		bool success;
		assembly {
			switch returndatasize()
				case 0 {
					// This is a non-standard ERC-20
					success := not(0) // set success to true
				}
				case 32 {
					// This is a compliant ERC-20
					returndatacopy(0, 0, 32)
					success := mload(0) // Set `success = returndata` of external call
				}
				default {
					// This is an excessively non-compliant ERC-20, revert.
					revert(0, 0)
				}
		}
		require(success, "TOKEN_TRANSFER_IN_FAILED");

		// Calculate the amount that was *actually* transferred
		uint256 balanceAfter = token.balanceOf(address(this));
		require(balanceAfter >= balanceBefore, "TOKEN_TRANSFER_IN_OVERFLOW");
		return balanceAfter - balanceBefore; // underflow already checked above, just subtract
	}

	function doTransferOut(
		address addr,
		address payable to,
		uint256 amount
	) internal {
		EIP20NonStandardInterface token = EIP20NonStandardInterface(addr);
		token.transfer(to, amount);

		bool success;
		assembly {
			switch returndatasize()
				case 0 {
					// This is a non-standard ERC-20
					success := not(0) // set success to true
				}
				case 32 {
					// This is a complaint ERC-20
					returndatacopy(0, 0, 32)
					success := mload(0) // Set `success = returndata` of external call
				}
				default {
					// This is an excessively non-compliant ERC-20, revert.
					revert(0, 0)
				}
		}
		require(success, "TOKEN_TRANSFER_OUT_FAILED");
	}

	function tryHalving() internal {
		uint256 currentBlockNumber = getCurrentBlockNumber();
		if (currentBlockNumber >= nextHalvingBlock && mainPool.lastBlockNumber < nextHalvingBlock) {
			rewardsPerBlock = rewardsPerBlock.div(2);
			halvingStartBlock = currentBlockNumber;
			nextHalvingBlock = halvingStartBlock.add(BLOCKS_PER_YEAR);
		}
	}

	function tokensMinedMainPool(uint256 startBlock, uint256 endBlock) internal view returns (uint256) {
		return (endBlock - startBlock).mul(rewardsPerBlock);
	}

	function getCurrentBlockNumber() internal view returns (uint256) {
		return block.number;
	}

	function valueToPower(uint256 value) internal pure returns (uint256) {
		return value;
	}

	function valueToBorrowPower(uint256 value) internal pure returns (uint256) {
		return value.mul(3).div(2);
	}

	function safeAddUnsignedSigned(uint256 current, int256 addend) internal pure returns (uint256) {
		if (addend >= 0) {
			return current + uint256(addend);
		}
		uint256 result = current - uint256(-addend);
		if (result > current) {
			return 0;
		}
		return result;
	}

	function safeAddSigned(int256 a, int256 b) internal pure returns (int256) {
		int256 c = a + b;
		if (b < 0) {
			require(c < a, "HDSDistributor::safeAddSigned: addition overflow");
		}
		if (b > 0) {
			require(c > a, "HDSDistributor::safeAddSigned: addition overflow");
		}
		return c;
	}
}
